//ignore bad data
if (!msg.payload.CurrentSummation || msg.payload.CurrentSummation===null || msg.payloadCurrentSummation==='' )
{
    return;
} else {

    //parse response
    strConsumptionTotal=msg.payload.CurrentSummation.SummationDelivered[0];
    intConsumptionTotal=parseInt(strConsumptionTotal,16);

    strMultiplier=msg.payload.CurrentSummation.Multiplier[0];
    intMultiplier=parseInt(strMultiplier,16);

    strDivisor=msg.payload.CurrentSummation.Divisor[0];
    intDivisor=parseInt(strDivisor,16);

    if (intConsumptionTotal>2147483647) {
        intConsumptionTotal = 0;
    }

    if (intMultiplier===0) {
        intMultiplier = 1;
    } 

    if (intDivisor===0) {
        intDivisor = 1;
    }

    strTimeStamp = msg.payload.CurrentSummation.TimeStamp[0];
    intTimeStamp = parseInt(strTimeStamp,16);
    timestamp=new Date("2000");//rainforest timestamp is based upon seconds since 1/1/2000
    //the following would also work:
    //timestamp=new Date("2000-01-01T00:00:00Z");//rainforest timestamp is based upon 1/1/2000
    //node.warn(timestamp.toUTCString());
    timestamp.setUTCSeconds(timestamp.getUTCSeconds() + intTimeStamp);
    //node.warn(timestamp.toUTCString());



    msg.payload = (intConsumptionTotal * intMultiplier) / intDivisor;
    msg.topic = "power/consumption/total";
    msg.timestamp = timestamp;

    if (msg.payload === 0)
    {
        return;
    } else {
        return msg;
    }
}

