//Node has 2 outputs - 1 for itteration and 1 for completion
var nextObj, out;
var itt = msg.iterationInfo;


//If the iterating has not yet begun set up the iteration metadata in the msg
if (typeof itt === 'undefined') {
    //Make sure payload is an array
    if( Object.prototype.toString.call(msg.payload) !== '[object Array]' ) {
       msg.payload = [msg.payload];
    }

    msg.iterationInfo = itt = {};
    itt.index = -1;
    itt.inArray = msg.payload;
    itt.outArray = [];

//Otherwise just push the input to the output array
} else {
    itt.outArray.push(msg.payload)
}

//Goto next object
itt.index ++;

//If there are stil objects left to iterate goto the next one in the original array
if (itt.index < itt.inArray.length) {
    nextObj = msg;
    msg.payload = itt.inArray[itt.index];

//otherwise pass the out array as the payload
} else {
    out = msg;
    msg.payload = itt.outArray;
    delete msg.iterationInfo;
}

return [nextObj, out];

var timestampfromthermostat;//timestamp
var thermostat = [];
var thermostatname = "";
var msgarr = [];
var remotesensor = [];
var remotesensorname;
var capability = [];
var capabilitytype;
var capabilityvalue;

for (thermostat of msg.payload.thermostatList) {//array so use "of" not "in"
    timestampfromthermostat = new Date(thermostat.utcTime+" UTC").getTime();
    thermostatname = thermostat.name.replace(/\s+/g, '').toLowerCase();//remove spaces, if any, and set to lowercase.
    //node.warn("completed replace with thermostatname = "+ thermostatname);

    if (thermostat.equipmentStatus === "") {
        thermostat.equipmentStatus = "off";
    }
    msgarr.push({
        payload:(thermostat.equipmentStatus),
        topic: thermostatname + "/thermostat/status",
        timestamp : timestampfromthermostat
    });    

    msgarr.push({
        payload:(thermostat.runtime.actualTemperature/10),
        topic: thermostatname + "/average/temperature",
        timestamp : timestampfromthermostat
    });

    msgarr.push({
        payload:(thermostat.runtime.desiredCool/10),
        topic: thermostatname + "/average/temperature/setpoint/cool",
        timestamp : timestampfromthermostat
    });
    msgarr.push({
        payload:(thermostat.runtime.desiredHeat/10),
        topic: thermostatname + "/average/temperature/setpoint/heat",
        timestamp : timestampfromthermostat
    });
    msgarr.push({
        payload:(thermostat.runtime.actualHumidity),
        topic: thermostatname + "/average/humidity",
        timestamp : timestampfromthermostat
    });

    // for weather stuff we only want the current weather (array element zero)
    msgarr.push({
        payload:(thermostat.weather.forecasts[0].temperature/10),
        topic: thermostatname + "/outside/temperature",
        timestamp : timestampfromthermostat
    });
    msgarr.push({
        payload:(thermostat.weather.forecasts[0].pressure/10),
        topic: thermostatname + "/outside/pressure",
        timestamp : timestampfromthermostat
    });
    msgarr.push({
        payload:(thermostat.weather.forecasts[0].relativeHumidity),
        topic: thermostatname + "/outside/humidity",
        timestamp : timestampfromthermostat
    });
    msgarr.push({
        payload:(thermostat.weather.forecasts[0].windSpeed/1000),
        topic: thermostatname + "/outside/wind/speed",
        timestamp : timestampfromthermostat
    });
    msgarr.push({
        payload:(thermostat.weather.forecasts[0].windBearing/10),
        topic: thermostatname + "/outside/wind/bearing",
        timestamp : timestampfromthermostat
    });

    for (remotesensor of thermostat.remoteSensors) {//array so use "of" not "in"
        remotesensorname = remotesensor.name.replace(/\s+/g, '').toLowerCase();//remove spaces, if any, and set to lowercase.
        if (remotesensorname==thermostatname) {
            if (remotesensorname=="home") { //my ecobee main unit is in the dining room
                remotesensorname="diningroom";
            } else {
                remotesensorname = "main";
            }
        }
        //node.warn("remotesensorname = " + remotesensorname);
        for (capability of remotesensor.capability) { //array so use "of" not "in"
            capabilitytype = capability.type.replace(/\s+/g, '').toLowerCase();//remove spaces, if any, and set to lowercase.
            //node.warn("capabilitytype = " + capabilitytype);

            capabilityvalue = capability.value;
            if (capabilitytype=="temperature") {
                capabilityvalue = capabilityvalue/10;//temperatures need decimal shifted place left
            }

            msgarr.push({
                payload:capabilityvalue,
                topic: thermostatname + "/" + remotesensorname + "/" + capabilitytype,
                timestamp : timestampfromthermostat
            });
        }
    }
}

return [msgarr];

