    "Authorization" : "Bearer aThisIsMyFakeTokenYoursWillBeLonger-AjsTIEvRKKQ",
    "Content-type" : "application/json"
    };
msg.url = "https://online.wonderware.com/apis/upload/datasource";
msg.method = "POST";
*/

msg.headers = {
    "Content-type" : "application/json"
};
msg.url = "https://online.wonderware.com/apis/upload/datasource";
msg.method = "POST";

//move Athorization inside headers:
msg.headers.Authorization = msg.Authorization;  //copy Authorization to be under headers
delete msg.Authorization;                       // and remove from old location
return msg;


return msg;

var METARdata = msg.payload.response.data[0].METAR[0];
var wind = METARdata.wind_speed_kt[0];
var gusts = wind; //if wind_gust_kt is not present, value defaults to wind_speed_kt
   if (typeof METARdata.wind_gust_kt != "undefined") {
	   gusts = METARdata.wind_gust_kt[0];
       }
var altim = METARdata.altim_in_hg[0];
var pressure = Math.round(altim * 33.8637526); //convert hg to mb
var dew = METARdata.dewpoint_c[0];
var temp = METARdata.temp_c[0];

//August-Roche-Magnus approximation to calculate humidity
var constA = 17.625;
var constB = 243.04;
var rh_numer = 100.0*Math.exp((constA*eval(dew))/(eval(dew)+constB));
var rh_denom = Math.exp((constA*eval(temp))/(eval(temp)+constB));
//var rh_numer = 100.0*Math.exp((constA*(dew))/((dew)+constB));
//var rh_denom = Math.exp((constA*(temp))/((temp)+constB));
var rh_hum   = (rh_numer/rh_denom);
var humidity = Math.round(rh_hum);

msg.payload = ((wind) + (",") + (gusts) + (",") + (humidity) + (",") + (pressure) + (",") + (temp));
return msg;

