    dtstring = new Date.parse(msg.observation_time)
} else {dtstring = new Date().toISOString();}

msgarr.push({
    payload:(temp_f),
    topic: "airport/" + station + "/temperature",
    timestamp : dtstring
});

var pressure = parseFloat(msg.payload.altim_in_hg[0]); //inches hg
var dew = parseFloat(msg.payload.dewpoint_c[0]);
var temp = parseFloat(msg.payload.temp_c[0]);

//August-Roche-Magnus approximation to calculate humidity
var constA = 17.625;
var constB = 243.04;
//var rh_numer = 100.0*Math.exp((constA*eval(dew))/(eval(dew)+constB));
//var rh_denom = Math.exp((constA*eval(temp))/(eval(temp)+constB));
var rh_numer = 100.0*Math.exp((constA*(dew))/((dew)+constB));
var rh_denom = Math.exp((constA*(temp))/((temp)+constB));
var rh_hum   = (rh_numer/rh_denom);
var humidity = Math.round(rh_hum);

msgarr.push({
    payload:(humidity),
    topic: "airport/" + station + "/humidity",
    timestamp : dtstring
});    

msgarr.push({
    payload:(pressure),
    topic: "airport/" + station + "/pressure",
    timestamp : dtstring
});    


return [msgarr];

